DROP TABLE IF EXISTS `character_counters`;
CREATE TABLE `character_counters` (
  `char_id` int(11) NOT NULL,
  `_PvP` int(11) NOT NULL DEFAULT '0',
  `_PK` int(11) NOT NULL DEFAULT '0',
  `_Highest_Karma` int(11) NOT NULL DEFAULT '0',
  `_Times_Died` int(11) NOT NULL DEFAULT '0',
  `_Players_Ressurected` int(11) NOT NULL DEFAULT '0',
  `_Duels_Won` int(11) NOT NULL DEFAULT '0',
  `_Monsters_Killed` int(11) NOT NULL DEFAULT '0',
  `_Raids_Killed` int(11) NOT NULL DEFAULT '0',
  `_Champions_Killed` int(11) NOT NULL DEFAULT '0',
  `_Crafted_Recipes` int(11) NOT NULL DEFAULT '0',
  `_Failed_Crafting` int(11) NOT NULL DEFAULT '0',
  `_Olympiad_Games_Won` int(11) NOT NULL DEFAULT '0',
  `_Longest_Kill_Spree` int(11) NOT NULL DEFAULT '0',
  `_Kill_Sprees_Ended` int(11) NOT NULL DEFAULT '0',
  `_Enchant_Item` int(11) NOT NULL DEFAULT '0',
  `_Crits_Done` int(11) NOT NULL DEFAULT '0',
  `_Mcrits_Done` int(11) NOT NULL DEFAULT '0',
  `_Get_Married` int(11) NOT NULL DEFAULT '0',
  `_Castle_Sieges_Won` int(11) NOT NULL DEFAULT '0',
  `_Dominion_Sieges_Won` int(11) NOT NULL DEFAULT '0',
  `_Fortress_Sieges_Won` int(11) NOT NULL DEFAULT '0',
  `_Players_Killed_In_Siege` int(11) NOT NULL DEFAULT '0',
  `_Players_Killed_In_Dominion` int(11) NOT NULL DEFAULT '0',
  `_Players_Killed_At_Fortress` int(11) NOT NULL DEFAULT '0',
  `_Enchanted_Normal_Scroll` int(11) NOT NULL DEFAULT '0',
  `_Enchanted_Blessed_Scroll` int(11) NOT NULL DEFAULT '0',
  `_Became_Hero` int(11) NOT NULL DEFAULT '0',
  `_Manor_Seeds_Sow` int(11) NOT NULL DEFAULT '0',
  `_Fish_Caught` int(11) NOT NULL DEFAULT '0',
  `_Treasure_Boxes_Opened` int(11) NOT NULL DEFAULT '0',
  `_Antharas_Killed` int(11) NOT NULL DEFAULT '0',
  `_Baium_Killed` int(11) NOT NULL DEFAULT '0',
  `_Valakas_Killed` int(11) NOT NULL DEFAULT '0',
  `_Orfen_Killed` int(11) NOT NULL DEFAULT '0',
  `_Ant_Queen_Killed` int(11) NOT NULL DEFAULT '0',
  `_Core_Killed` int(11) NOT NULL DEFAULT '0',
  `_Beleth_Killed` int(11) NOT NULL DEFAULT '0',
  `_Sailren_Killed` int(11) NOT NULL DEFAULT '0',
  `_Baylor_Killed` int(11) NOT NULL DEFAULT '0',
  `_Main_Events` int(11) NOT NULL DEFAULT '0',
  `_Mini_Events` int(11) NOT NULL DEFAULT '0',
  `_Captured_Flags` int(11) NOT NULL DEFAULT '0',
  `_Returned_Flags` int(11) NOT NULL DEFAULT '0',
  `_First_Blood` int(11) NOT NULL DEFAULT '0',
  `_Chests_Opened` int(11) NOT NULL DEFAULT '0',
  `_Killed_Mutants` int(11) NOT NULL DEFAULT '0',
  `_Killed_Zombies` int(11) NOT NULL DEFAULT '0',
  `_Domination_Points` int(11) NOT NULL DEFAULT '0',
  `_Times_Voted` int(11) NOT NULL DEFAULT '0',
  `_Achievements_Done` int(11) NOT NULL DEFAULT '0',
  `_Summons_Killed` int(11) NOT NULL DEFAULT '0',
  PRIMARY KEY (`char_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT;
